import React,{useRef, useEffect, useState} from 'react';
import "../assets/css/canvas.css";

function ImageCanvas(props) {
    const [boxes, setBoxes] = useState([]);
    const [canvas, setCanvas] = useState();
    const [ctx, setCtx] = useState();
    // const canvas = document.getElementById("imageCanvas");
    // const ctx = canvas.getContext("2d");
    const canvasRef = useRef();
    // useEffect(() => {
    //     const canvas = canvasRef.current;
    //     setCtx(canvas.getContext("2d"));

    //     ctx.fillStyle = "green";
    //     ctx.fillRect(10, 10, 150, 100);
    //     ctx.fillRect(20, 20, 180, 130);
    // },[])



    ///////////////////// for drawing one box ////////////////////////////
    // calculate the x-y of the canvas
    var canvasOffset = canvas.offset();
    var offsetX = canvasOffset.left;
    var offsetY = canvasOffset.top;
    var scrollX = canvasRef.current.scrollLeft();
    var scrollY = canvasRef.current.scrollTop();
    //  flag: where user is dragging the box
    const [isDown, setIsDown] = useState(false);
    const [startX, setStartX] = useState();
    const [startY, setStartY] = useState();

    const [prevStartX, setPrevStartX] = useState(0);
    const [prevStartY, setPrevStartY] = useState(0);

    const [prevWidth, setPrevWidth] = useState(0);
    const [prevHeight, setPrevHeight] = useState(0);    


    const handleMouseDown = (e) => {
        e.preventDefault();
        e.stopPropagation();

        // save the starting x/y of the rectangle
        setStartX( parseInt(e.clientX - offsetX) );
        setStartY(parseInt(e.clientY - offsetY));

        // set a flag indicating the drag has begun
        setIsDown(true);
    };

    const handleMouseUp = (e) => {
        e.preventDefault();
        e.stopPropagation();

        // the drag is over, clear the dragging flag
        setIsDown(false);
        ctx.strokeRect(prevStartX, prevStartY, prevWidth, prevHeight);
    }

    const handleMouseMove = (e) => {
        e.preventDefault();
        e.stopPropagation();

        // if we're not dragging, just return
        if (!isDown) {
            return;
        }

        // get the current mouse position
        let mouseX = parseInt(e.clientX - offsetX);
        let mouseY = parseInt(e.clientY - offsetY);

        // Put your mousemove stuff here



        // calculate the rectangle width/height based
        // on starting vs current mouse position
        var width = mouseX - startX;
        var height = mouseY - startY;

            // clear the canvas
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // draw a new rect from the start position 
        // to the current mouse position
        ctx.strokeRect(startX, startY, width, height);

            setPrevStartX(startX);
            setPrevStartY(startY);

            setPrevWidth(width);
            setPrevHeight(height);
    }


    const handleMouseOut = (e) => {
        e.preventDefault();
        e.stopPropagation();

        // the drag is over, clear the dragging flag
        setIsDown(false);
    }


    return (
        <canvas 
            id="imageCanvas"
            ref={canvasRef} 
            onMouseDown={handleMouseDown} 
            onMouseUp={handleMouseUp}
            onMouseMove={handleMouseMove}
            onMouseOut={handleMouseOut}
        ></canvas>
    );
}

export default ImageCanvas;
