// components/ChatBox.js

import React, { useState } from 'react';
import { IoSendSharp } from 'react-icons/io5'; // Import the send icon
import '../assets/css/ChatBox.css';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');

  const handleUserMessage = async () => {
    if (!userInput.trim()) return;

    // Send user message to OpenAI API and get a response


    // Update messages with user message and OpenAI response
    setMessages([
      ...messages,
      { role: 'user', content: userInput },
    ]);

    // Clear user input after sending the message
    setUserInput('');
  };

  return (
    <div className="chat-box">
      <div className="chat-box-title">Conversation</div>
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.role}>
            <div className="avatar">{msg.name.charAt(0)}</div>
            <div className="message-content">
              <div className="name">{msg.name}</div>
              {msg.content}
            </div>
          </div>
        ))}
      </div>
      <div className="user-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
        />
        <button onClick={handleUserMessage} className="send-button">
          <IoSendSharp size={22} color="#fff" /> {/* Use the IoSendSharp icon */}
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
