import React from 'react';
import Xarrow, {useXarrow, Xwrapper} from 'react-xarrows';
import Draggable from 'react-draggable';
import "../assets/css/graphPanel.css";


const boxStyle = {border: 'grey solid 2px', borderRadius: '10px', padding: '5px', height:"20px", width:"100px"};
// draggable box
const Entity = ({id}) => {
  const updateXarrow = useXarrow();
  return (
      <Draggable onDrag={updateXarrow} onStop={updateXarrow}>
          <div id={id} style={boxStyle}>
              {id}
          </div>
      </Draggable>
  );
};


function GraphPanel(props) {
  return (
    <div className="graph-panel-container">
      {/* You can add settings display functionality here */}
      {/* <h2>Scene Graph</h2> */}
        {/* {props.imageSrc  &&  <img src={props.imageSrc} alt="a sample img" className='graph-panel-img'></img>} */}
          <Xwrapper>
                  <DraggableBox id={'elem1'}/>
                  <DraggableBox id={'elem2'}/>
                  <Xarrow start={'elem1'} end="elem2"/>
            </Xwrapper>

    </div>
  );
}

export default GraphPanel;
