// components/MoviePanel.js

import React,{useRef, useState, forwardRef, useImperativeHandle} from 'react';
import ImageCanvas from './ImageCanvas';
import VideoPlayer from './VideoPlayer';


const MoviePanel = forwardRef( (props, ref) => {
  const [showCanvas, setShowCanvas] = useState(false);

  //toggle show canvas
  function toggleShowCanvas() {
    console.log(showCanvas)
    setShowCanvas(!showCanvas);
  }

  // pass toggle 
  useImperativeHandle(ref, () => {
    return {
      toggleShowCanvas,
    }
  }, [])



  // Placeholder content for the MoviePanel
  const imageCanvasRef = useRef();
  const callCleanBox = () => {
    if(imageCanvasRef.current){
      imageCanvasRef.current.cleanBoxes();
    }
  }

  return (
    <div className="movie-panel-container">
      {/* <h2>Video Panel</h2> */}
      <VideoPlayer></VideoPlayer>

      {
        showCanvas ? 
        (
          <>          
            <div className='movie-btn-list'>
              <button className='clean-box-button' onClick={callCleanBox}>Clean Boxes</button> 
              <button className='clean-box-button' onClick={callCleanBox}>Start Conversation</button> 
              <button className='clean-box-button' onClick={callCleanBox}>Continue Video</button> 
            </div>
            <ImageCanvas ref={imageCanvasRef}></ImageCanvas>
          </>

        ):
        ""

      }



    </div>
  );
});

export default MoviePanel;
