// App.js

import React, { useRef, useState } from 'react';
import './App.css';

import ChatBox from './components/ChatBox';
import MoviePanel from './components/MoviePanel';
import GraphPanel from './components/GraphPanel';

import buddingBoxIcon from "./assets/icon/draw.svg";
import insSegIcon from "./assets/icon/set.svg";
import uploadIcon from "./assets/icon/upload.svg";

const App = () => {
  const moviePanelRef = useRef();
  const [showCanvas, setShowCanvas] = useState(false);
  //toggle show canvas
  function toggleShowCanvas() {
    setShowCanvas(!showCanvas);
  }

  const pauseVideo = () => {
    if(moviePanelRef.current) moviePanelRef.current.pauseVideo();
  }
  const captureScreenshot = () => {
    if(moviePanelRef.current) moviePanelRef.current.captureScreenshot();
  }
  const handleBunddingBox = (e) => {
    e.preventDefault();
    if(!showCanvas){
      toggleShowCanvas();
      pauseVideo();
      captureScreenshot();
    }
  }
  const uploadVideo = () => {

  }


  return (
    <div className="app-container">
      <div className="app-header">
        <h2>Natural Language Information Retrieval from Domain-Specific Videos</h2>
      </div>
      <div className="movie-panel">
        <MoviePanel ref={moviePanelRef} showCanvas={showCanvas} toggleShowCanvas={toggleShowCanvas}/>
      </div>
      <div className="graph-panel">
        <GraphPanel />
      </div>

      <div className="settings-panel">
        <div></div>
        <div id="upload-wrapper">
          <input type="file" id="videoInput" accept="video/*" className='setting-btn' onClick={uploadVideo}></input>  
          <div> 
            <img src={uploadIcon} alt="icon for upload video button"></img>
            <p>Upload Video</p>
          </div>  
        </div>
        <button className='setting-btn' onClick={handleBunddingBox}>
          <img src={buddingBoxIcon} alt="icon for budding box button"></img>
          <p>Budding Box</p>
        </button>
        <button className='setting-btn'>
          <img src={insSegIcon} alt="icon for instance segmentation button"></img>
          <p>Instance Seg</p>
        </button>
      </div>
      <div className="chatbox">
        {/* Chatbox Panel */}
        <ChatBox />
      </div>
    </div>
  );
};

export default App;
