import React,{useState, useRef, forwardRef, useImperativeHandle} from 'react';
import ReactPlayer from 'react-player'
import Slider from '@mui/material/Slider';
import { Button } from '@mui/material';
import "../assets/css/video.css";
import IconStop from "../assets/icon/stop.svg";
import IconContinue from "../assets/icon/continue.svg";

import myVide from '../assets/video/sample_video.mp4' 

const VideoPlayer= forwardRef((props, ref) => {
    const [duration, setDuration] = useState(0);
    const [played, setPlayed] = useState(0);
    const playerRef = useRef(null);
    const [playing, setPlaying] = useState(false);

    // load total length of vidoe
    const handleDuration = (duration) => {
      setDuration(duration);
    };  

    // load current played seconds
    const handleOnProgress = (data) => {
        console.log(data)
        setPlayed(data.playedSeconds)
    }

    // move video on slider bar change
    const handleSliderChange = (event, newValue) => {
        setPlayed(newValue);
        if (playerRef.current) {
            playerRef.current.seekTo(newValue);
        }
    };
    // stop/play
    const togglePlayPause = () => {
        setPlaying(!playing);
    };

    //get screenshot
    const captureScreenshot = () => {
        if (playerRef.current) {
            // Try to access the video element directly
            const videoElement = playerRef.current.getInternalPlayer('player');

            // Make sure it's a video element
            if (videoElement && videoElement.tagName === 'VIDEO') {
                const canvas = document.createElement('canvas');
                canvas.width = videoElement.videoWidth;
                canvas.height = videoElement.videoHeight;

                const ctx = canvas.getContext('2d');
                ctx.drawImage(videoElement, 0, 0, canvas.width, canvas.height);

                const image = canvas.toDataURL('../assets/img');
                // downloadImage(image, 'screenshot.png');
            } else {
                console.error('Unable to access video element');
            }
        }
    };

    // 
    useImperativeHandle(ref, () => {
        return {
            pauseVideo(){
                setPlaying(false);
            },
            captureScreenshot,
        }
    }, [])


    return (
        <div className='video-player'>
            <ReactPlayer 
                ref={playerRef}
                url={myVide}
                onDuration={handleDuration}
                onProgress={handleOnProgress}
                playing={playing}
            />
            {
                props.showCanvas ?  "" :       
                <div className='video-player-menu'>
                    <Button variant="contained"  onClick={togglePlayPause}>
                        {
                            playing ?                     
                            <img src={IconStop} alt="btn stop icon" className='icon-stop'></img>
                            :
                            <img src={IconContinue} alt="btn stop continue"></img>
                        }

                    </Button>
                    {/* <Button variant="contained" onClick={captureScreenshot}>screen shot</Button> */}
                    <Slider 
                        defaultValue={0} 
                        max={duration} 
                        aria-label="Default" 
                        value={played} 
                        onChange={handleSliderChange}
                        valueLabelDisplay="auto"
                        // onClick={seekToTime(10)}
                    />
                </div>
            }

        </div>

    );
})

export default VideoPlayer;
