import React,{useRef, useEffect, useState} from 'react';
import "../assets/css/canvas.css";

function ImageCanvas(props) {
    const [boxes, setBoxes] = useState([]);
    const [canvas, setCanvas] = useState();
    const [ctx, setCtx] = useState();
    // const [canvasBox, setCanvasBox] = useState();
    const [ctxBox, setCtxBox] = useState();
    // calculate the x-y of the canvas
    const [offsetX, setOffsetX] = useState();
    const [offsetY, setOffsetY] = useState();

    const canvasRef = useRef();
    const canvasBoxRef = useRef();
    useEffect(() => {
        const imageCanvas = canvasRef.current;
        setCanvas(imageCanvas);
        setCtx(imageCanvas.getContext("2d"));
        setOffsetX(imageCanvas.getBoundingClientRect().left);
        setOffsetY(imageCanvas.getBoundingClientRect().top)

        console.log( "offsetX " ,imageCanvas.offsetLeft)

        imageCanvas.getContext("2d").strokeStyle = "green";
        imageCanvas.getContext("2d").strokeRect(10, 10, 50, 60);

        setCtxBox(canvasBoxRef.current.getContext("2d"));


    },[])




    ///////////////////// for drawing one box ////////////////////////////
    //  flag: where user is dragging the box
    const [isDown, setIsDown] = useState(false);
    const [startX, setStartX] = useState();
    const [startY, setStartY] = useState();

    const [prevStartX, setPrevStartX] = useState(0);
    const [prevStartY, setPrevStartY] = useState(0);

    const [prevWidth, setPrevWidth] = useState(0);
    const [prevHeight, setPrevHeight] = useState(0);    



    const handleMouseUp = (e) => {
        e.preventDefault();
        e.stopPropagation();

        // the drag is over, clear the dragging flag
        setIsDown(false);
        ctxBox.strokeRect(prevStartX, prevStartY, prevWidth, prevHeight);
    }

    const handleMouseDown = (e) => {
        e.preventDefault();
        e.stopPropagation();

        const rect = canvasRef.current.getBoundingClientRect();
        const currentOffsetX = rect.left;
        const currentOffsetY = rect.top;

        setStartX(parseInt(e.clientX - currentOffsetX + window.scrollX));
        setStartY(parseInt(e.clientY - currentOffsetY + window.scrollY));

        setIsDown(true);
    };

    const handleMouseMove = (e) => {
        e.preventDefault();
        e.stopPropagation();

        if (!isDown) {
            return;
        }

        const rect = canvasRef.current.getBoundingClientRect();
        const currentOffsetX = rect.left;
        const currentOffsetY = rect.top;

        let mouseX = parseInt(e.clientX - currentOffsetX + window.scrollX);
        let mouseY = parseInt(e.clientY - currentOffsetY + window.scrollY);

        var width = mouseX - startX;
        var height = mouseY - startY;

        ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
        ctx.strokeRect(startX, startY, width, height);

        setPrevStartX(startX);
        setPrevStartY(startY);
        setPrevWidth(width);
        setPrevHeight(height);
    };

    const handleMouseOut = (e) => {
        e.preventDefault();
        e.stopPropagation();

        // the drag is over, clear the dragging flag
        setIsDown(false);
    }


    return (
        <div id='canvas-wrapper'>
            <canvas id="canvaxBox" ref={canvasBoxRef} height={500} width={500}></canvas>
            <canvas 
                id="imageCanvas"
                ref={canvasRef} 
                onMouseDown={handleMouseDown} 
                onMouseUp={handleMouseUp}
                onMouseMove={handleMouseMove}
                onMouseOut={handleMouseOut}
            ></canvas>

        </div>

    );
}

export default ImageCanvas;
