import React, { useState,  forwardRef, useImperativeHandle } from 'react';
import Draggable from 'react-draggable';
import {useXarrow} from 'react-xarrows';


const SceneBox = ((props, ref) => {
    const [position, setPosition] = useState({ x: props.x, y: props.y });
    const boxStyle = {border: 'grey solid 2px', borderRadius: '10px', padding: '5px', height:"20px", width:"100px", color:props.color};
    const updateXarrow = useXarrow();



    // Function to reset the position
    const resetPosition = () => {
        setPosition({ x: props.x, y: props.y });
    };

    useImperativeHandle(ref, () => ({
        resetPosition(){
            resetPosition()
        }
    }))


    return (
      <Draggable onDrag={updateXarrow} onStop={updateXarrow} defaultPosition={position}>
          <div id={props.id} style={boxStyle}> 
              {props.name}
          </div>
      </Draggable>
    );
});

export default SceneBox;
